import React, {Component} from 'react';
import axios from 'axios';


class ShowDetails extends Component{

    static async getInitialProps(props){
        const showid = props.query.showid; 
        const res = await axios.get(`http://api.tvmaze.com/shows/${showid}?embed=cast`);
        
        return { show: res.data};
    }

    render(){
        
        const {name,image} = this.props.show;
        return(
            <div className="showdetails">
                <div className="showdetails_poster" style={{backgroundImage: `url(${image.original})`}}>

                </div>
            
                <h1>{name}</h1>
                <style jsx>{`
                    .showdetails_poster{
                        height: 30%;
                        width: 100%;
                        background-size: cover;
                    }
                `}</style> 
            </div>
        );
    }
}

export default ShowDetails;
