import React, {Component } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import Thumbnail from '../../components/Thumbnail';


class CountryTest extends Component{

    static async getInitialProps(props){
        const country = props.query.country;
        const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2019-12-01`);
        console.log(response.data);
        return {shows: response.data };
    }

    renderShows = () => {

        const shows = this.props.shows;
        return shows.map((show, index) => {
            return(
                 <li key={index}><Thumbnail show={show}/></li>
            );
        });

    }

    render(){
        return(
        <ul className="tvshows">{this.renderShows()}</ul>
        );
    }
    
}
export default CountryTest;
