import React, {Component } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import Thumbnail from '../../components/Thumbnail';
import Header from '../../components/Header';


class CountryTest extends Component{

    static async getInitialProps(props){
        const country = props.query.country;
        const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-12-01`);
        
        return {shows: response.data, country: country };
    }

    renderShows = () => {

        const shows = this.props.shows;
        return shows.map((showItem, index) => {
            const {show} = showItem;
            
            return(
                <li key={index}>
                    <Thumbnail 
                        imageUrl={(show.image && show.image.medium) || undefined} 
                        caption={show.name}
                        herf = "/[country]/[showid]"
                        as = {`/${this.props.country}/${show.id}`}
                    />
                </li>
            );
        });

    }

    render(){
        return(
        <div className="home">
            <ul className="tvshows-grid">
                {this.renderShows()}

                <style jsx>{`
                    .tvshows-grid{
                        display: grid;
                        grid-template-columns: 1fr 1fr 1fr 1fr;
                        gap: 20px;
                        padding: 0;
                        margin: 0;
                        background-color: ciyan;
                        list-style-type: none;
                    }
                `}</style>
            </ul>
        </div>
        );
    }
    
}
export default CountryTest;
