import React, {Component} from 'react';
import axios from 'axios';
import parse from 'html-react-parser';
import Cast from '../../components/Cast'; 


class ShowDetails extends Component{

    static async getInitialProps(props){
        const showid = props.query.showid; 
        const res = await axios.get(`http://api.tvmaze.com/shows/${showid}?embed=cast`);
        console.log(res.data);
        return { show: res.data};
    }

    render(){
        
        const {name, summary, _embedded} = this.props.show;
        const image = (this.props.show.image.original || undefined); 
        return(
            <div className="showdetails container">
                <div className="row">
                    <img src={image} className="showdetails_poster col-4" ></img>
                    <div className="col-8">
                        <h1 className="showdetails_name">{name}</h1>
                        { parse(summary)}
                    </div>
                </div>
                <div className="row">
                    <div className="cast-box">
                     <Cast cast={_embedded.cast} />
                    </div>
                </div>    
                  
                <style jsx>{`
                    
                    .container{ 
                        margin:auto;
                        display: grid;
                        width: 95%;
                        border: 1px solid;
                      
                    }
                    .col-4{
                        width: 30%;
                        float: left;
                        padding:10px;
                        border: 1px solid;
                        
                    }
                    .col-8{
                       
                        padding-left : 10px;
                        margin: auto;
                        border: 1px solid;
                       
                    }
                    .row{
                        border: 1px solid;
                    }
                    .cast-box{
                        width: 1400px;
                    }
                    @media screen and (max-width: 1000px) {
                        .cast-box{
                            width: 700px;
                        }
                        .col-8{
                            float:left;
                           
                            border: 1px solid;
                           
                        }
                    }
                   
                   
                `}</style> 
            </div>
        );
    }
}

export default ShowDetails;
