import React, {Component} from 'react';
import Thumbnail from '../Thumbnail';

class Cast extends Component{

    renderCast = () =>{
        const {cast} = this.props;
        return cast.map((castItem, index)=>{
            const {image, name} = castItem;
            return(
                <li>
                    <Thumbnail
                          imageUrl={(image && show.image.medium) || undefined} 
                          caption={name}
                    />
                </li>
            );
        })
    };


    render(){
        return(
            <div className="cast">
                <h3>Cast</h3>
                <ul className="cast_list">
                    {this.renderCast()}
                </ul>
            </div>
        );
    }
}

export default Cast;
