import React, {Component } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import Thumbnail from '../../components/Thumbnail';


class CountryTest extends Component{

    static async getInitialProps(props){
        const country = props.query.country;
        const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-12-01`);
        
        return {shows: response.data };
    }

    renderShows = () => {

        const shows = this.props.shows;
        return shows.map((showItem, index) => {
            const {show} = showItem;
            
            return(
                <li key={index}>
                    <Thumbnail 
                        imageUrl={(show.image && show.image.medium) || undefined} 
                        caption={show.name}
                    />
                </li>
            );
        });

    }

    render(){
        return(
        <ul className="tvshows-grid">
            {this.renderShows()}

            <style jsx>{`
                .tvshows-grid{
                    display: grid;
                    grid-template-columns: 1fr 1fr 1fr;
                    gap: 20px;
                    padding: 0;
                    margin: 0;
                    
                }
            `}</style>
        </ul>
        );
    }
    
}
export default CountryTest;
