import React, {Component} from 'react';
import axios from 'axios';

class ShowDetails extends Component{

    static async getInitialProps(props){
        console.log(props);
        const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-12-01`);
        
        return {shows: response.data, country: country };
    }

    render(){
        const {name , image} = this.props;
        return(
            <div className="show-details">
                <div 
                    className="show-details_poster"
                    style={{backgroundImage: `url(${image.medium})`}}
                >
                </div>
                <h1>{name}</h1>

                <style jsx>{`{
                    .show-dateils_poster{
                        height: 200px;
                        background-size: cover;
                    }
                }`}</style>
            </div>

        );
    }
}



export default ShowDetails;
