import React, {Component } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import Thumbnail from '../../components/Thumbnail';


class CountryTest extends Component{

    static async getInitialProps(props){
        const country = props.query.country;
        const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-11-01`);
        
        return {shows: response.data };
    }

    renderShows = () => {

        const shows = this.props.shows;
        return shows.map((showItem, index) => {
            const {show} = showItem;
            
            return(
                <li key={index}>
                     <Thumbnails imageUrl={show.image.medium} caption={show.name}/>
                </li>
            );
        });

    }

    render(){
        return(
        <ul className="tvshows">{this.renderShows()}</ul>
        );
    }
    
}
export default CountryTest;
