import React, {Component } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import { render } from 'react-dom';

class CountryTest extends Component{

    static async getInitialProps(props){
        const response = await axios.get('http://api.tvmaze.com/schedule?country=US&date=2014-12-01');
        console.log(response.data);
        return {shows: response.data };
    }

    renderShows = () => {

        const shows = this.props.shows;
        return shows.map((show, index) => {
            return(
                 <li key={index}>{show.show.name}</li>
            );
        });

    }

    render(){
        return(
        <ul className="tvshows">{this.renderShows}</ul>
        );
    }
    
}
export default CountryTest;
